mapper.insert:
INSERT INTO BOARDLIST
(
    BOARD_TITLE
    , BOARD_CONTENT
    , BOARD_WRITER
    , BOARD_REGDATE
    , BOARD_VIEWCOUNT
    , BOARD_PW
    , BOARD_DELETEYN
) VALUES (
             #{boardTitle}
         ,  #{boardContent}
         ,  #{boardWriter}
         ,  NOW()
         ,  0
         ,  #{boardPw}
         ,  'N'
         )

mapper.update:
UPDATE BOARDLIST
SET BOARD_MODIDATE = NOW()
  , BOARD_TITLE = #{boardTitle}
  , BOARD_CONTENT = #{boardContent}
where board_no = #{boardNo}

UPDATE BoardList
SET BOARD_VIEWCOUNT = BOARD_VIEWCOUNT + 1
WHERE BOARD_NO = #{boardNo}

mapper.select:
SELECT BOARD_PW
FROM BoardList
WHERE BOARD_NO = #{boardNo}
